import React, { useState } from "react";
import SelectRecommendation from "./SelectRecommendation";
import styled from "styled-components";

export default function Main() {
  const [currentStep, setCurrentStep] = useState(0);
  const handleSelectRecommendation = () => {
    setCurrentStep(1);
  };

  return (
    <div>
      <OptionButton onClick={() => onSelect("preference")}>
        취향대로 추천
      </OptionButton>
      <OptionButton onClick={() => onSelect("random")}>랜덤 추천</OptionButton>
      {currentStep === 1 && (
        <SelectRecommendation onSelect={handleSelectRecommendation} />
      )}
    </div>
  );
}

const OptionButton = styled.div`
  // 여기에 스타일 추가
`;
