import React from "react";
import styled from "styled-components";

const FoodTypeOption = styled.button`
  cursor: pointer;
  padding: 10px 20px;
  margin: 10px;
  border: 2px solid transparent;
  background-color: #f0f0f0;
  transition: background-color 0.3s, color 0.3s;

  border-color: ${(props) => (props.selected ? "green" : "transparent")};

  &:hover {
    background-color: #d1d1d1;
    color: #fff;
  }
`;

export default function FoodTypeSelection({
  onSelectFoodType,
  selectedFoodType,
  goBack,
  goForward,
}) {
  return (
    <div>
      <FoodTypeOption
        onClick={() => onSelectFoodType("RICE")}
        selected={selectedFoodType === "RICE"}
      >
        밥
      </FoodTypeOption>
      <FoodTypeOption
        onClick={() => onSelectFoodType("NOODLES")}
        selected={selectedFoodType === "NOODLES"}
      >
        면
      </FoodTypeOption>
      <FoodTypeOption
        onClick={() => onSelectFoodType("MEAT_AND_SEAFOOD")}
        selected={selectedFoodType === "MEAT_AND_SEAFOOD"}
      >
        고기/해물
      </FoodTypeOption>
    </div>
  );
}
