// CuisineSelection.jsx
import React from "react";
import styled from "styled-components";

const CuisineOption = styled.button`
  cursor: pointer;
  padding: 10px 20px;
  margin: 10px;
  border: none;
  background-color: #f0f0f0;
  transition: background-color 0.3s, color 0.3s;
`;

export default function CuisineSelection({ onSelectCuisine }) {
  return (
    <div>
      <CuisineOption onClick={() => onSelectCuisine("KOREAN")}>
        한식
      </CuisineOption>
      <CuisineOption onClick={() => onSelectCuisine("CHINESE")}>
        중식
      </CuisineOption>
      <CuisineOption onClick={() => onSelectCuisine("JAPANESE")}>
        일식
      </CuisineOption>
    </div>
  );
}
