import React, { useState } from "react";
import StartScreen from "./StartScreen";
import CategoryScreen from "./CategoryScreen";
import MenuSelectionScreen from "./MenuSelectionScreen";
import FoodTypeSelection from "./FoodTypeSelect";

const Main = () => {
  const [screen, setScreen] = useState("start"); // 현재 화면 상태
  const [cuisine, setCuisine] = useState(""); // 사용자가 선택한 음식 종류

  const handleStartSelection = (selection) => {
    // '취향대로 추천' 선택 시
    if (selection === "preference") {
      setScreen("cuisineSelection");
    } else {
      // TODO '랜덤 추천' 선택 시, 랜덤 로직 구현

      setScreen("result");
    }
  };

  const handleCategorySelection = (type) => {
    setCuisine(type); // 사용자가 선택한 음식 종류 상태 업데이트
    setScreen("menuSelection");
  };

  const handleMenuSelection = (menu) => {
    // 메뉴 선택 후 결과 화면으로
    // ...
    setScreen("result");
  };

  switch (screen) {
    case "start":
      return <StartScreen onSelect={handleStartSelection} />;
    case "cuisineSelection":
      return <CategoryScreen onSelect={handleCategorySelection} />;
    case "menuSelection":
      return (
        <FoodTypeSelection cuisine={cuisine} onSelect={handleMenuSelection} />
      );
    //   case 'result':
    //     // 결과 화면 컴포넌트
    //     // ...
    //     return <div>선택한 메뉴: ...</div>;
    default:
      return <div>잘못된 접근입니다.</div>;
  }
};
export default Main;
