// CuisineSelection.jsx
import React from "react";
import styled from "styled-components";
import { GoBack_and_GoForward } from "../button/Button";

export default function CuisineSelection({
  onSelectCuisine,
  selectedCuisine,
  goBack,
  goForward,
}) {
  return (
    <div>
      <CuisineOption
        onClick={() => onSelectCuisine("KOREA")}
        selected={selectedCuisine === "KOREA"}
      >
        한식
      </CuisineOption>
      <CuisineOption
        onClick={() => onSelectCuisine("CHINA")}
        selected={selectedCuisine === "CHINA"}
      >
        중식
      </CuisineOption>
      <CuisineOption
        onClick={() => onSelectCuisine("JAPAN")}
        selected={selectedCuisine === "JAPAN"}
      >
        일식
      </CuisineOption>
      <div>
        <GoBack_and_GoForward onClick={goBack}>이전으로</GoBack_and_GoForward>
        <GoBack_and_GoForward onClick={goForward} disabled={!selectedCuisine}>
          다음으로
        </GoBack_and_GoForward>
      </div>
    </div>
  );
}

const CuisineOption = styled.button`
  cursor: pointer;
  padding: 3rem 6rem;
  margin: 3rem;
  border: 2px solid transparent;
  background-color: #f0f0f0;
  transition: background-color 0.3s, color 0.3s;

  border-color: ${(props) => (props.selected ? "green" : "transparent")};

  &:hover {
    background-color: #d1d1d1;
    color: #fff;
  }
`;

