// CuisineSelection.jsx
import React from "react";
import styled from "styled-components";
import {
  buttonHoverStyle,
  buttonSelectedStyle,
} from "../../styles/commonStyles";

export default function CuisineSelection({
  onSelectCuisine,
  selectedCuisine,
  goBack,
  goForward,
}) {
  return (
    <div>
      <CuisineOption
        onClick={() => onSelectCuisine("KOREA")}
        selected={selectedCuisine === "KOREA"}
      >
        한식
      </CuisineOption>
      <CuisineOption
        onClick={() => onSelectCuisine("CHINA")}
        selected={selectedCuisine === "CHINA"}
      >
        중식
      </CuisineOption>
      <CuisineOption
        onClick={() => onSelectCuisine("JAPAN")}
        selected={selectedCuisine === "JAPAN"}
      >
        일식
      </CuisineOption>
      <div>
        <Button onClick={goBack}>이전으로</Button>
        <Button onClick={goForward} disabled={!selectedCuisine}>
          다음으로
        </Button>
      </div>
    </div>
  );
}

const CuisineOption = styled.button`
  cursor: pointer;
  padding: 1rem 2rem;
  margin: 0.5rem;
  border: none;
  background-color: #f0f0f0;
  transition: background-color 0.3s, color 0.3s;

  ${buttonHoverStyle}

  ${(props) => props.selected && buttonSelectedStyle}
`;

const Button = styled.button`
  cursor: pointer;
  padding: 1rem 2rem;
  margin: 0.5rem;
  border: none;
  background-color: #f0f0f0;
  transition: background-color 0.3s, color 0.3s;

  ${buttonHoverStyle}

  ${(props) => props.selected && buttonSelectedStyle}


  &:disabled {
    background-color: #ccc;
    cursor: default;
  }
`;
