import React from "react";
import styled from "styled-components";

// 스타일 컴포넌트 정의
const StartContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100vh;
`;

const Button = styled.button`
  padding: 10px 20px;
  margin: 10px;
  font-size: 16px;
  cursor: pointer;
`;

// StartScreen 컴포넌트
function StartScreen({ onSelect }) {
  return (
    <StartContainer>
      <h1>오늘의 점심 메뉴를 추천해드립니다!</h1>
      <Button onClick={() => onSelect("preference")}>취향대로 추천</Button>
      <Button onClick={() => onSelect("random")}>랜덤 추천</Button>
    </StartContainer>
  );
}

export default StartScreen;
