import React from "react";
import styled from "styled-components";

const CuisineContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100vh;
`;

const CuisineButton = styled.button`
  padding: 10px 20px;
  margin: 10px;
  font-size: 16px;
  cursor: pointer;
`;

function CategoryScreen({ onSelect }) {
  return (
    <CuisineContainer>
      <h1>어떤 음식을 선호하시나요?</h1>
      <CuisineButton onClick={() => onSelect("korean")}>한식</CuisineButton>
      <CuisineButton onClick={() => onSelect("chinese")}>중식</CuisineButton>
      <CuisineButton onClick={() => onSelect("japanese")}>일식</CuisineButton>
    </CuisineContainer>
  );
}

export default CategoryScreen;
