import { useEffect, useState } from "react";
import styled from "styled-components";
import {
  buttonHoverStyle,
  buttonSelectedStyle,
} from "../../styles/commonStyles";

export default function ChoiceScreen({ onChoice, recommendationType }) {
  const [selected, setSelected] = useState(recommendationType);

  useEffect(() => {
    setSelected(recommendationType);
  }, [recommendationType]);

  const handleClick = (choice) => {
    setSelected(choice); // 선택된 버튼을 state에 설정
    onChoice(choice); // 상위 컴포넌트의 함수를 호출하여 선택된 값 전달
  };
  return (
    <div>
      <ChoiceButton
        selected={selected === "preference"}
        onClick={() => handleClick("preference")}
      >
        취향대로 추천
      </ChoiceButton>
      <ChoiceButton
        selected={selected === "random"}
        onClick={() => handleClick("random")}
      >
        랜덤 추천
      </ChoiceButton>
    </div>
  );
}
const ChoiceButton = styled.button`
  cursor: pointer;
  padding: 1rem 2rem;
  margin: 0.5rem;
  border: none;
  background-color: #f0f0f0;
  transition: background-color 0.3s, color 0.3s;

  ${buttonHoverStyle}

  ${(props) => props.selected && buttonSelectedStyle}
`;
