import React, { useEffect, useState } from "react";
import { menuOptions } from "../../constants/constants";

const RandomSelect = () => {
  const [time, setTime] = useState(3);

  useEffect(() => {
    time > 0 && setTimeout(() => setTime(time - 1), [time]);
  });

  const cuisines = Object.keys(menuOptions); // 'KOREA', 'JAPAN', 'CHINA'
  const randomCuisine = cuisines[Math.floor(Math.random() * cuisines.length)];
  const types = Object.keys(menuOptions[randomCuisine]); // 'RICE', 'NOODLE', 'MEAT_OR_SEAFOOD'
  const randomType = types[Math.floor(Math.random() * types.length)];
  const options = Object.keys(menuOptions[randomCuisine][randomType]);
  const randomOption = options[Math.floor(Math.random() * options.length)];
  const randomDish = menuOptions[randomCuisine][randomType][randomOption];
  //   alert(`랜덤 추천 음식은: ${randomDish} 입니다.`); // 랜덤 음식 알림
  return (
    <div>
      {time ? time : <div>`랜덤 추천 음식은 ${randomDish} 입니다.`</div>}
    </div>
  );
};

export default RandomSelect;
