import React, { useState } from "react";
import StartScreen from "./StartScreen";
import CategoryScreen from "./CategoryScreen";
import MenuSelectionScreen from "./MenuSelectionScreen";
import FoodTypeSelection from "./FoodTypeSelect";

const Main = () => {
  const [step, setStep] = useState(1); // 현재 단계
  const [selections, setSelections] = useState({
    recommendationType: "",
    cuisine: "",
    dishType: "",
    soup: "",
  });

  const handleNext = (selection) => {
    setSelections((prevSelections) => ({
      ...prevSelections,
      ...selection,
    }));
    setStep((prevStep) => prevStep + 1);
  };

  const handleBack = () => {
    setStep((prevStep) => prevStep - 1);
  };

  const handleReset = () => {
    setSelections({
      recommendationType: "",
      cuisine: "",
      dishType: "",
      soup: "",
    });
    setStep(1);
  };

  switch (step) {
    case 1:
      return <StepOne onChoose={handleNext} />;
    case 2:
      return (
        <StepTwo
          onChoose={handleNext}
          onBack={handleBack}
          currentSelection={selections.cuisine}
        />
      );
    // ... 이하 단계별 컴포넌트 설정
    default:
      return <FinalStep selection={selections} onReset={handleReset} />;
  }
};

// StepOne, StepTwo, ... 컴포넌트를 각각 정의합니다.
