import React from "react";
import styled from "styled-components";
import { StepBtn } from "../button/Button";

export default function FoodTypeSelection({
  onSelectFoodType,
  selectedFoodType,
  goBack,
  goForward,
}) {
  return (
    <div>
      <FoodTypeOption
        onClick={() => onSelectFoodType("RICE")}
        selected={selectedFoodType === "RICE"}
      >
        밥
      </FoodTypeOption>
      <FoodTypeOption
        onClick={() => onSelectFoodType("NOODLE")}
        selected={selectedFoodType === "NOODLE"}
      >
        면
      </FoodTypeOption>
      <FoodTypeOption
        onClick={() => onSelectFoodType("MEAT_OR_SEAFOOD")}
        selected={selectedFoodType === "MEAT_OR_SEAFOOD"}
      >
        고기/해물
      </FoodTypeOption>
      <div>
        <StepBtn onClick={goBack}>이전으로</StepBtn>
        <StepBtn onClick={goForward} disabled={!selectedFoodType}>
          다음으로
        </StepBtn>
      </div>
    </div>
  );
}

const FoodTypeOption = styled.button`
  cursor: pointer;
  padding: 3rem 6rem;
  margin: 3rem;
  border: 2px solid transparent;
  background-color: #f0f0f0;
  transition: background-color 0.3s, color 0.3s;

  border-color: ${(props) => (props.selected ? "green" : "transparent")};

  &:hover {
    background-color: #d1d1d1;
    color: #fff;
  }
`;

const Button = styled.button`
  cursor: pointer;
  padding: 3rem 6rem;
  margin: 3rem;
  background-color: #f0f0f0;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #d1d1d1;
  }

  // 비활성화된 버튼 스타일
  &:disabled {
    background-color: #ccc;
    cursor: default;
  }
`;
