import { ThemeProvider } from "styled-components";
import Header from "./components/header/Header";
import Main from "./components/main/Main";
import { theme } from "./styles/theme";
import { useState } from "react";

function App() {
  const [currentStep, setCurrentStep] = useState(1); // 1: 첫 화면, 2: 카테고리 선택, 3: 세부 카테고리 선택, 4: 국물 선택, 5: 최종 추천
  const [selectedCuisine, setSelectedCuisine] = useState("");
  const [selectedType, setSelectedType] = useState("");
  const [selectedOption, setSelectedOption] = useState("");
  const [finalRecommendation, setFinalRecommendation] = useState("");
  const [recommendationType, setRecommendationType] = useState(null); // 'preference' 또는 'random'

  const resetSelections = () => {
    setSelectedCuisine("");
    setSelectedType("");
    setSelectedOption("");
    setFinalRecommendation("");
  };

  const handleRestart = () => {
    if (recommendationType === "preference") {
      resetSelections();
    }
    setCurrentStep(1);
  };
  return (
    <ThemeProvider theme={theme}>
      <div>
        <Header currentStep={currentStep} onRestart={handleRestart} />
        <Main currentStep={currentStep} setCurrentStep={setCurrentStep} />
      </div>
    </ThemeProvider>
  );
}

export default App;
