// CuisineSelection.jsx
import React from "react";
import styled from "styled-components";

export default function CuisineSelection({
  onSelectCuisine,
  selectedCuisine,
  goBack,
  goForward,
}) {
  return (
    <div>
      <CuisineOption
        onClick={() => onSelectCuisine("KOREAN")}
        selected={selectedCuisine === "KOREAN"}
      >
        한식
      </CuisineOption>
      <CuisineOption
        onClick={() => onSelectCuisine("CHINESE")}
        selected={selectedCuisine === "CHINESE"}
      >
        중식
      </CuisineOption>
      <CuisineOption
        onClick={() => onSelectCuisine("JAPANESE")}
        selected={selectedCuisine === "JAPANESE"}
      >
        일식
      </CuisineOption>
      <div>
        <Button onClick={goBack}>이전으로</Button>
        <Button onClick={goForward} disabled={!selectedCuisine}>
          다음으로
        </Button>
      </div>
    </div>
  );
}

const CuisineOption = styled.button`
  cursor: pointer;
  padding: 10px 20px;
  margin: 10px;
  border: none;
  background-color: #f0f0f0;
  transition: background-color 0.3s, color 0.3s;
  // 활성화된 옵션을 표시합니다.
  border-color: ${(props) => (props.selected ? "blue" : "transparent")};

  &:hover {
    background-color: #d1d1d1;
    color: #fff;
  }
`;

const Button = styled.button`
  cursor: pointer;
  padding: 10px 20px;
  margin: 10px;
  background-color: #f0f0f0;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #d1d1d1;
  }

  // 비활성화된 버튼 스타일
  &:disabled {
    background-color: #ccc;
    cursor: default;
  }
`;
