import React, { useState } from "react";
import ChoiceScreen from "./ChoiceScreen";
import styled from "styled-components";
import { menuOptions } from "../../constants/constants";
import CuisineSelection from "./CuisineSelection";
import FoodTypeSelection from "./FoodTypeSelection";
import SoupSelection from "./SoupSelection";
import RandomSelect from "./RandomSelect";

export default function Main({ currentStep, setCurrentStep, onRestart }) {
  // const [currentStep, setCurrentStep] = useState(1); // 1: 첫 화면, 2: 카테고리 선택, 3: 세부 카테고리 선택, 4: 국물 선택, 5: 최종 추천
  const [selectedCuisine, setSelectedCuisine] = useState("");
  const [selectedType, setSelectedType] = useState("");
  const [selectedOption, setSelectedOption] = useState("");
  const [finalRecommendation, setFinalRecommendation] = useState("");
  const [recommendationType, setRecommendationType] = useState(null); // 'preference' 또는 'random'

  const handleStartClick = () => {
    if (recommendationType === "preference") {
      setCurrentStep(2); // '취향대로 추천'을 선택한 경우, 다음 단계로
    } else if (recommendationType === "random") {
      setCurrentStep(6);
      // '랜덤 추천'을 선택한 경우, 랜덤 음식 추천 로직
      // const cuisines = Object.keys(menuOptions); // 'KOREA', 'JAPAN', 'CHINA'
      // const randomCuisine =
      //   cuisines[Math.floor(Math.random() * cuisines.length)];
      // const types = Object.keys(menuOptions[randomCuisine]); // 'RICE', 'NOODLE', 'MEAT_OR_SEAFOOD'
      // const randomType = types[Math.floor(Math.random() * types.length)];
      // const options = Object.keys(menuOptions[randomCuisine][randomType]);
      // const randomOption = options[Math.floor(Math.random() * options.length)];
      // const randomDish = menuOptions[randomCuisine][randomType][randomOption];
      // alert(`랜덤 추천 음식은: ${randomDish} 입니다.`); // 랜덤 음식 알림
      // time > 0 && setTimeout(() => setTime(time - 1), 1000);
    } else {
      alert("추천 유형을 선택해 주세요."); // 선택이 안되어 있을 때
    }
  };

  const handleChoice = (choice) => {
    setRecommendationType(choice); // 'preference' 또는 'random' 선택 상태 저장
  };

  // const handleCuisineSelection = (cuisine) => {
  //   setSelectedCuisine(cuisine);
  //   setCurrentStep(currentStep + 1); // 다음 단계로 이동
  // };

  const goBack = () => {
    setCurrentStep(currentStep - 1);
  };

  const goForward = () => {
    setCurrentStep(currentStep + 1);
  };

  const handleSoupSelection = (soupOption) => {
    if (
      menuOptions[selectedCuisine] &&
      menuOptions[selectedCuisine][selectedType]
    ) {
      setSelectedOption(soupOption);
      const finalDish = menuOptions[selectedCuisine][selectedType][soupOption];
      setFinalRecommendation(finalDish);
      setCurrentStep(5); // 최종 추천 단계로 이동
    } else {
      // 오류 처리 로직
      console.error("선택한 음식 유형이 존재하지 않습니다.");
    }
  };

  // const resetSelections = () => {
  //   setSelectedCuisine("");
  //   setSelectedType("");
  //   setSelectedOption("");
  //   setFinalRecommendation("");
  // };

  // const handleRestart = () => {
  //   if (recommendationType === "preference") {
  //     resetSelections();
  //   }
  //   setCurrentStep(1);
  // };

  return (
    <div>
      {currentStep === 1 && (
        <>
          <ChoiceScreen
            onChoice={handleChoice}
            recommendationType={recommendationType}
          />
          <StartButton
            onClick={handleStartClick}
            disabled={!recommendationType}
          >
            Start
          </StartButton>
        </>
      )}
      {currentStep === 2 && (
        <CuisineSelection
          onSelectCuisine={setSelectedCuisine}
          selectedCuisine={selectedCuisine}
          goBack={goBack}
          goForward={goForward}
        />
      )}
      {currentStep === 3 && (
        <FoodTypeSelection
          onSelectFoodType={setSelectedType}
          selectedFoodType={selectedType}
          goBack={goBack}
          goForward={goForward}
        />
      )}

      {currentStep === 4 && (
        <SoupSelection onSelectSoup={handleSoupSelection} goBack={goBack} />
      )}

      {currentStep === 5 && (
        <div>
          <h2>오늘의 메뉴 추천</h2>
          <p>{finalRecommendation}</p>
          <ChoiceButton onClick={onRestart}>다시 시작</ChoiceButton>
        </div>
      )}
      {currentStep === 6 && (
        <RandomSelect
          setFinalRecommendation={setFinalRecommendation}
          setCurrentStep={setCurrentStep}
        />
      )}
    </div>
  );
}

export const ChoiceButton = styled.button`
  cursor: pointer;
  padding: 1rem 2rem;
  margin: 0.5rem;
  border: none;
  background-color: ${(props) => props.theme.colors.third};
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #e0e0e0;
  }
`;

const StartButton = styled.button`
  cursor: pointer;
  padding: 1rem 2rem;
  margin: 0.5;
  border: none;
  background-color: #f0f0f0;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #e0e0e0;
  }
`;
