import { useState } from "react";
import styled, { css } from "styled-components";

export default function ChoiceScreen({ onChoice }) {
  const [selected, setSelected] = useState("");
  const handleClick = (choice) => {
    setSelected(choice); // 선택된 버튼을 state에 설정
    onChoice(choice); // 상위 컴포넌트의 함수를 호출하여 선택된 값 전달
  };
  return (
    <div>
      <ChoiceButton
        selected={selected === "preference"}
        onClick={() => handleClick("preference")}
      >
        취향대로 추천
      </ChoiceButton>
      <ChoiceButton
        selected={selected === "random"}
        onClick={() => handleClick("random")}
      >
        랜덤 추천
      </ChoiceButton>
    </div>
  );
}
const ChoiceButton = styled.button`
  cursor: pointer;
  padding: 10px 20px;
  margin: 10px;
  border: none;
  background-color: #f0f0f0;
  transition: background-color 0.3s, color 0.3s;

  &:hover {
    background-color: #d1d1d1;
    color: #fff;
  }

  // 선택된 버튼에 대한 추가 스타일
  ${(props) =>
    props.selected &&
    css`
      background-color: #d1d1d1; // 선택 시 배경색 변경
      color: #fff; // 선택 시 글자색 변경
      &:hover {
        background-color: #bcbcbc; // 호버 시 더 어두운 색으로 변경
      }
    `}
`;
