import styled from "styled-components";

export const ChoiceButton = styled.button`
  cursor: pointer;
  padding: 1rem 2rem;
  margin: 0.5rem;
  border: none;
  background-color: ${(props) => props.theme.colors.third};
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #e0e0e0;
  }
`;
