import React, { useState } from "react";

function StepOne({ onChoose }) {
  // '취향대로 추천'과 '랜덤 추천' 중 현재 선택된 값을 관리하는 로컬 상태
  const [recommendationType, setRecommendationType] = useState("");

  const handleSubmit = () => {
    if (recommendationType) {
      // 부모 컴포넌트의 onChoose 함수를 호출하여 선택된 값을 전달
      onChoose({ recommendationType });
    } else {
      // 사용자가 선택하지 않았다면 알림
      alert("옵션을 선택해주세요.");
    }
  };

  return (
    <div>
      <h1>점심 메뉴 추천</h1>
      <div>
        <label>
          <input
            type="radio"
            name="recommendationType"
            value="custom"
            checked={recommendationType === "custom"}
            onChange={() => setRecommendationType("custom")}
          />
          취향대로 추천
        </label>
      </div>
      <div>
        <label>
          <input
            type="radio"
            name="recommendationType"
            value="random"
            checked={recommendationType === "random"}
            onChange={() => setRecommendationType("random")}
          />
          랜덤 추천
        </label>
      </div>
      <button onClick={handleSubmit}>Start</button>
    </div>
  );
}

export default StepOne;
