import React, { useState } from "react";
import SelectRecommendation from "./SelectRecommendation";
import SelectCuisine from "./SelectCuisine";

export default function Main() {
  const [currentStep, setCurrentStep] = useState(1);
  const [selections, setSelections] = useState({
    recommendation: "",
    cuisine: "",
  });

  const handleSelectRecommendation = (recommendation) => {
    setSelections((prev) => ({ ...prev, recommendation }));
    setCurrentStep(2); // 취향대로 추천이나 랜덤 추천을 선택하면 다음 단계로 이동
  };

  const handleSelectCuisine = (cuisine) => {
    setSelections((prev) => ({ ...prev, cuisine }));
    // 다음 단계로 이동하는 로직이 필요하다면 여기에 추가
  };

  const handlePrevious = () => {
    setCurrentStep(currentStep - 1);
  };

  return (
    <div>
      {currentStep === 1 && (
        <SelectRecommendation onSelect={handleSelectRecommendation} />
      )}
      {currentStep === 2 && selections.recommendation === "preference" && (
        <SelectCuisine onSelect={handleSelectCuisine} />
      )}

      {/* 이전 및 다음 버튼 */}
      {currentStep > 1 && <button onClick={handlePrevious}>이전으로</button>}
      {/* 여기에 다음으로 버튼을 추가하고, 필요한 로직을 구현 */}
    </div>
  );
}
