import { useState } from "react";
import styled from "styled-components";

export default function ChoiceScreen({ onChoice }) {
  const [selected, setSelected] = useState("");
  const handleClick = (choice) => {
    setSelected(choice); // 선택된 버튼을 state에 설정
    onChoice(choice); // 상위 컴포넌트의 함수를 호출하여 선택된 값 전달
  };
  return (
    <div>
      <ChoiceButton onClick={() => onChoice("preference")}>
        취향대로 추천
      </ChoiceButton>
      <ChoiceButton onClick={() => onChoice("random")}>랜덤 추천</ChoiceButton>
    </div>
  );
}

const ChoiceButton = styled.button`
  cursor: pointer;
  padding: 10px 20px;
  margin: 10px;
  border: none;
  background-color: #f0f0f0;
  transition: background-color 0.3s, color 0.3s; // 여기에 color 변화도 transition에 추가합니다.

  &:hover {
    background-color: #d1d1d1; // 호버 시 배경색 변경
    color: #fff; // 호버 시 글자색 변경
  }
`;
